mapper.select:
/* intrAuthInqy10101010 : 권한 목록 조회 */ SELECT AUTH_CD , AUTH_NM , USE_YN , DISP_ORDER , REMARK FROM INTR_AUTH WHERE 1=1
AND REGEXP_REPLACE(LOWER(AUTH_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
ORDER BY AUTH_CD

/* intrAuthInqy30101020 : 사용자 권한 목록 조회 */ SELECT DATA.* FROM ( SELECT AE.AUTH_CD , A.AUTH_NM , AE.EMP_IDX , E.EMP_NM , D.DEPT_NM , AE.DISP_ORDER FROM INTR_AUTH_EMP AE LEFT OUTER JOIN INTR_EMP E ON E.EMP_IDX = AE.EMP_IDX LEFT OUTER JOIN INTR_AUTH A ON A.AUTH_CD = AE.AUTH_CD AND A.USE_YN = 'Y' LEFT OUTER JOIN INTR_DEPT D ON D.DEPT_CD = E.DEPT_CD WHERE 1=1
AND A.AUTH_CD = #{authCd, jdbcType=VARCHAR}
) DATA ORDER BY DATA.EMP_NM, DATA.EMP_IDX

mapper.insert:
/* intrAuthProc10101010 : 권한 등록 처리 */
INSERT INTO INTR_AUTH
(
			AUTH_CD
		,	AUTH_NM
		,	USE_YN
		,	DISP_ORDER
		,	REMARK
)
VALUES
(
		(SELECT 
			'AUTH_' || LPAD(NVL(MAX(SUBSTR(AUTH_CD,LENGTH(AUTH_CD)-3,LENGTH(AUTH_CD))),0) + 1,4,0) AS AUTH_CD 
		 FROM INTR_AUTH
		)
	,	#{authNm, jdbcType=VARCHAR}
	,	#{useYn, jdbcType=VARCHAR}
	,	(
			SELECT 
				NVL(MAX(DISP_ORDER),0) + 1 
			FROM INTR_AUTH
		)
	,	#{remark, jdbcType=VARCHAR}
)

mapper.update:
/* intrAuthProc10201010 : 권한 수정 처리 */
UPDATE INTR_AUTH
	SET		AUTH_NM = #{authNm, jdbcType=VARCHAR}
		,	USE_YN = #{useYn, jdbcType=VARCHAR}
		,	REMARK = #{remark, jdbcType=VARCHAR}
WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}

mapper.delete:
/* intrAuthProc10301010 : 권한 삭제 처리 */
DELETE FROM INTR_AUTH
	WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}

/* intrAuthProc20101010 : 메뉴 권한 삭제 처리 */
DELETE FROM INTR_AUTH_MENU
	WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}

mapper.insert:
/* intrAuthProc20101020 : 메뉴 권한 등록 처리 */
INSERT INTO INTR_AUTH_MENU
(
			AUTH_CD
		,	MENU_CD
		,	USE_YN
		,	DISP_ORDER
		,	REMARK
)
VALUES
(
		#{authCd, jdbcType=VARCHAR}
	,	#{menuCd, jdbcType=VARCHAR}
	,	'Y'
	,	(
			SELECT 
				NVL(MAX(DISP_ORDER),0) + 1 
			FROM INTR_AUTH_MENU WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}
		)
	,	NULL
)

mapper.delete:
/* intrAuthProc30101010 : 사용자 권한 삭제 처리 */
DELETE FROM INTR_AUTH_EMP
	WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}

mapper.insert:
/* intrAuthProc30101020 : 사용자 권한 등록 처리 */
INSERT INTO INTR_AUTH_EMP
(
			AUTH_CD
		,	EMP_IDX
		,	USE_YN
		,	DISP_ORDER
		,	REMARK
)
VALUES
(
		#{authCd, jdbcType=VARCHAR}
	,	#{empIdx, jdbcType=VARCHAR}
	,	'Y'
	,	(
			SELECT 
				NVL(MAX(DISP_ORDER),0) + 1 
			FROM INTR_AUTH_EMP WHERE AUTH_CD = #{authCd, jdbcType=VARCHAR}
		)
	,	NULL
)
