mapper.select:
/* intrAprvInqy10101010 : 결재내역 목록 조회 */ SELECT SUB_DATA.* , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN FROM ( SELECT APRV.APRV_IDX , APRV.EMP_IDX , EMP.EMP_NM , DEPT.DEPT_NM , APRV_TITLE , APRV.REG_DT AS APRV_REG_DT , APRV.REG_TM AS APRV_REG_TM , RANK() OVER(ORDER BY APRV.APRV_IDX) AS NUM , COUNT(APRV.APRV_IDX) OVER() AS LIST_CNT , (SELECT COUNT(CONTENT_IDX) FROM INTR_FILE WHERE CONTENT_IDX = APRV.APRV_IDX AND USE_YN = 'Y' ) AS FILE_CNT FROM INTR_APRV APRV LEFT OUTER JOIN INTR_EMP EMP ON APRV.EMP_IDX = EMP.EMP_IDX LEFT OUTER JOIN INTR_DEPT DEPT ON DEPT.DEPT_CD = EMP.DEPT_CD AND DEPT.USE_YN = 'Y' WHERE 1=1 AND APRV.USE_YN = 'Y'
AND REGEXP_REPLACE(LOWER(APRV.APRV_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND APRV.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND APRV.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
) SUB_DATA WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.APRV_REG_DT DESC, SUB_DATA.APRV_REG_TM DESC
