mapper.insert:
/* intrAprvProc10101020 : 품의문 결재선 등록 처리 */ INSERT INTO INTR_APRV_LINE ( APRV_IDX , APRV_SNO , STEP_CD , APRV_EMP_IDX , LAST_APRV_YN , APRV_RSLT_DT , APRV_RSLT_TM , APRV_RSLT_RESN , USE_YN , DISP_ORDER , REMARK ) VALUES ( #{aprvIdx, jdbcType=VARCHAR} , LPAD(#{aprvSno, jdbcType=VARCHAR},4,0) , #{stepCd, jdbcType=VARCHAR} , #{aprvEmpIdx, jdbcType=VARCHAR} , #{lastAprvYn, jdbcType=VARCHAR}
, TO_CHAR(SYSDATE,'YYYYMMDD') , TO_CHAR(SYSDATE,'HH24MISS')
, NULL , NULL
, NULL , 'Y' , ( SELECT NVL(MAX(DISP_ORDER),0) + 1 FROM INTR_APRV_LINE WHERE APRV_IDX = #{aprvIdx, jdbcType=VARCHAR} ) , NULL )

mapper.update:
/* intrAprvProc10101021 : RSLT 데이터 NULL 처리 */
	UPDATE INTR_APRV_LINE
		SET 	APRV_RSLT_DT = NULL
			,	APRV_RSLT_TM = NULL
