mapper.select:
/* intrBoardInqy10101010 : 공지사항 목록 조회 */ SELECT SUB_DATA.NUM , SUB_DATA.BRD_IDX , SUB_DATA.BRD_TITLE , SUB_DATA.EMP_NM , SUB_DATA.DEPT_NM , SUB_DATA.LIST_CNT , SUB_DATA.REG_DT , SUB_DATA.BRD_READHIT , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN FROM ( SELECT BRD.BRD_IDX , BRD.BRD_TITLE , BRD.EMP_IDX , EMP.EMP_NM , DEPT.DEPT_NM , BRD.REG_DT , BRD.REG_TM , BRD.BRD_READHIT , RANK() OVER(ORDER BY BRD.BRD_IDX DESC) AS NUM , COUNT(BRD.BRD_IDX) OVER() AS LIST_CNT , (SELECT COUNT(CONTENT_IDX) FROM INTR_FILE WHERE CONTENT_IDX = BRD.BRD_IDX AND USE_YN = 'Y' ) AS FILE_CNT FROM INTR_BOARD BRD LEFT OUTER JOIN INTR_EMP EMP ON BRD.EMP_IDX = EMP.EMP_IDX LEFT OUTER JOIN INTR_DEPT DEPT ON DEPT.DEPT_CD = EMP.DEPT_CD AND DEPT.USE_YN = 'Y' WHERE 1 = 1 AND BRD.USE_YN = 'Y'
AND REGEXP_REPLACE(LOWER(BRD.BRD_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND BRD.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND BRD.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
) SUB_DATA WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.REG_DT DESC, SUB_DATA.REG_TM DESC

mapper.update:
/* intrBoardProc10201010 : 공지사항 삭제 처리 */
	UPDATE INTR_BOARD
		SET USE_YN = 'N'
	WHERE BRD_IDX IN

		#{delIdx}

/* intrBoardProc10103010 : 공지사항 수정 처리 */
	UPDATE INTR_BOARD
		SET 	BRD_TITLE = #{brdTitle, jdbcType=VARCHAR}
			,	BRD_CONTENT = #{brdContent, jdbcType=VARCHAR}
	WHERE BRD_IDX = #{contentIdx, jdbcType=VARCHAR}

/* intrBoardProc10104010 : 공지사항 조회수 증가 */
	UPDATE INTR_BOARD
		SET 	BRD_READHIT = BRD_READHIT + 1
	WHERE BRD_IDX = #{contentIdx, jdbcType=VARCHAR}
