mapper.insert:
		/* intrFileProc10101010 : 파일 데이터 저장 */
		INSERT INTO INTR_FILE
		(
				FILE_IDX
			,	CONTENT_IDX
			,	FILE_SNO
			,	FILE_ORGL_NM
			,	FILE_TYPE
			,	FILE_SIZE
			,	USE_YN
			,	DISP_ORDER
			,	REMARK		
		)
		VALUES
		(
				'FILE_'||LPAD(SEQ_FILE_IDX.NEXTVAL,4,0)
			,	#{contentIdx, jdbcType=VARCHAR}
			,	(
					SELECT 
                        LPAD(NVL(MAX(FILE_SNO),0) + 1,4,0)
					FROM INTR_FILE WHERE CONTENT_IDX = #{contentIdx, jdbcType=VARCHAR}
				)
			,	#{fileOrglNm, jdbcType=VARCHAR}
			,	#{fileType, jdbcType=VARCHAR}
			,	#{fileSize, jdbcType=VARCHAR}
			,	'Y'
			,	(
					SELECT 
						NVL(MAX(DISP_ORDER),0) + 1 
					FROM INTR_FILE
				)
			,	NULL
		)

mapper.update:
/* intrFileProc10101020 : 파일 데이터 수정 (CONTENT_IDX N처리) */
UPDATE INTR_FILE F1
	SET F1.USE_YN = 'N'
WHERE F1.CONTENT_IDX = #{contentIdx, jdbcType=VARCHAR}

/* intrFileProc10101020 : 파일 데이터 수정 (FILE_IDX Y처리) */
UPDATE INTR_FILE F1
	SET F1.USE_YN = 'Y'
WHERE F1.FILE_IDX = #{fileIdx, jdbcType=VARCHAR}

/* intrFileProc10101030 : 파일 데이터 삭제 */
UPDATE INTR_FILE
	SET USE_YN = 'N'
WHERE CONTENT_IDX IN

	#{delIdx}
