/* intrLoginInqy10301010 : 사용자 정보 찾기 */ SELECT EMP_IDX , DEPT_CD , GRADE_CD , EMP_NM , EMP_GENDER , EMP_PHONE , EMP_ADDR , EMP_ADDR_INFO , EMP_EMAIL , EMP_ID , EMP_PWD , EMP_HIRE_DATE , EMP_RESI_DATE FROM INTR_EMP WHERE 1=1 AND UPPER(EMP_EMAIL) = UPPER(#{findEmail, jdbcType=VARCHAR})
AND UPPER(EMP_ID) = UPPER(#{findId, jdbcType=VARCHAR})
AND EMP_NM = #{findNm, jdbcType=VARCHAR}
