mapper.select:
/* intrTaskInqy10101010 : 업무일지 목록 조회 */ SELECT T.TASK_CD , T.EMP_IDX , E.EMP_NM , D.DEPT_NM , G.GRADE_NM , T.TASK_CONTENT , T.REG_DT || T.REG_TM AS REG_DT_TM , T.REG_DT , T.REG_TM FROM INTR_TASK T LEFT OUTER JOIN INTR_EMP E ON E.EMP_IDX = T.EMP_IDX LEFT OUTER JOIN INTR_DEPT D ON D.DEPT_CD = E.DEPT_CD LEFT OUTER JOIN INTR_GRADE G ON G.GRADE_CD = E.GRADE_CD WHERE 1=1
AND T.EMP_IDX = #{empIdx, jdbcType=VARCHAR} AND T.REG_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
ORDER BY T.REG_DT DESC, T.REG_TM ASC, T.TASK_CD ASC

mapper.delete:
/* intrTaskProc10101010 : 업무일지 삭제 처리 */
DELETE FROM INTR_TASK
	WHERE EMP_IDX = #{empIdx, jdbcType=VARCHAR}
		AND REG_DT = TO_CHAR(SYSDATE,'YYYYMMDD')

mapper.insert:
/* intrTaskProc10101011 : 업무일지 등록 처리 */
INSERT INTO INTR_TASK
(
		TASK_CD
	,	EMP_IDX
	,	TASK_CONTENT
	,	REG_DT
	,	REG_TM
)
VALUES
(
		'TASK_' || LPAD(SEQ_TASK_IDX.NEXTVAL,4,0)
	,	#{empIdx, jdbcType=VARCHAR}
	,	#{taskContent, jdbcType=VARCHAR}
	,	#{regDt, jdbcType=VARCHAR}
	,	#{regTm, jdbcType=VARCHAR}
)
