mapper.select:
/* intrCoreInqy1011 : 메뉴 조회 */ WITH M_DATA AS ( SELECT DATA.* FROM ( SELECT DISTINCT MA.MENU_CD FROM AUTH A INNER JOIN EMP_INFO E ON E.EMP_IDX = #{idxSet, jdbcType=VARCHAR} AND A.USE_YN = 'Y' INNER JOIN EMP_AUTH EA ON EA.EMP_IDX = E.EMP_IDX INNER JOIN MENU_AUTH MA ON MA.AUTH_CD = EA.AUTH_CD GROUP BY MA.MENU_CD ) DATA WHERE 1=1 ) SELECT DATA.* , LEVEL AS LV FROM ( SELECT M.MENU_CD , M.MENUTYPE_CD , M.MENU_NM , M.UPPR_MENU_CD , UM.MENU_NM AS UPPR_MENU_NM , CASE WHEN M.UPPR_MENU_CD IS NULL THEN LEAD(M.MAPPING_ID) OVER(ORDER BY M.MENU_CD ASC) ELSE M.MAPPING_ID END AS MAPPING_ID , CASE WHEN M.UPPR_MENU_CD IS NULL THEN LEAD(M.MENU_CD) OVER(ORDER BY M.MENU_CD ASC) ELSE M.MENU_CD END AS MENU_SET , M.USE_YN , M.DISP_ORDER , M.REMARK FROM M_DATA INNER JOIN MENU M ON M.MENU_CD = M_DATA.MENU_CD LEFT OUTER JOIN MENU UM ON UM.MENU_CD = M.UPPR_MENU_CD WHERE 1=1 AND M.USE_YN = 'Y'
AND M.MENUTYPE_CD = '0'
AND M.MENUTYPE_CD = '1'
) DATA START WITH DATA.UPPR_MENU_CD IS NULL CONNECT BY PRIOR DATA.MENU_CD = DATA.UPPR_MENU_CD ORDER SIBLINGS BY DATA.MENU_CD
