mapper.select:
/* intrEmpInqy1012 : 사용자 정보 찾기 */ SELECT EMP_IDX , ORG_CD , RANK_CD , EMP_NM , IS_MALE , MOB_NO , ADDR , ADDR_INFO , EMAIL , EMP_ID , EMP_PWD , HIRE_DT , LEAV_DT FROM EMP_INFO WHERE 1=1 AND UPPER(EMAIL) = UPPER(#{findEmail, jdbcType=VARCHAR})
AND UPPER(EMP_ID) = UPPER(#{findId, jdbcType=VARCHAR})
AND EMP_NM = #{findNm, jdbcType=VARCHAR}

/* intrEmpInqy2031 : 부서 사원 트리 조회 */ SELECT SUB_DATA.* , CASE WHEN SUB_DATA.EMP_NM IS NULL THEN 'N' ELSE 'Y' END AS ISLEAF FROM ( SELECT DATA.* FROM ( SELECT O.ORG_CD , O.ORG_NM , O.UPPR_ORG_CD , '' AS RANK_CD , '' AS RANK_NM , LEVEL AS LV , '' AS EMP_IDX , '' AS EMP_NM , 0 AS ROW_NUM FROM ORG O WHERE O.USE_YN = 'Y' START WITH O.UPPR_ORG_CD IS NULL CONNECT BY PRIOR O.ORG_CD = O.UPPR_ORG_CD ORDER SIBLINGS BY O.ORG_CD ) DATA UNION SELECT E.ORG_CD , O.ORG_NM , O.UPPR_ORG_CD , E.RANK_CD AS RANK_CD , R.RANK_NM AS RANK_NM , 2 AS LV , E.EMP_IDX , E.EMP_NM , ROW_NUMBER() OVER(ORDER BY EMP_NM ASC) AS ROW_NUM FROM EMP_INFO E LEFT OUTER JOIN ORG O ON E.ORG_CD = O.ORG_CD LEFT OUTER JOIN RANK R ON E.RANK_CD = R.RANK_CD WHERE E.LEAV_DT IS NULL ) SUB_DATA WHERE 1=1
AND REGEXP_REPLACE(LOWER(SUB_DATA.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{popNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
ORDER BY SUB_DATA.ORG_CD ASC , SUB_DATA.LV ASC

mapper.insert:
/* intrEmpProc1011 : 사원 등록 처리 */
INSERT INTO EMP_INFO
(
		EMP_IDX
	,	ORG_CD
	,	RANK_CD
	,	EMP_NM
	,	IS_MALE
	,	MOB_NO
	,	ADDR
	,	ADDR_INFO
	,	EMAIL
	,	EMP_ID
	,	EMP_PWD
	,	HIRE_DT
	,	LEAV_DT
)
VALUES
(
		#{sequenceId, jdbcType=VARCHAR}
	,	#{setOrgCd, jdbcType=VARCHAR}
	,	#{setRankCd, jdbcType=VARCHAR}
	,	#{empNm, jdbcType=VARCHAR}
	,	#{isMale, jdbcType=VARCHAR}
	,	REPLACE(#{mobNo, jdbcType=VARCHAR},'-','')
	,	#{addr, jdbcType=VARCHAR}
	,	#{addrInfo, jdbcType=VARCHAR}
	,	#{email, jdbcType=VARCHAR}
	,	#{empId, jdbcType=VARCHAR}
	,	#{empPwd, jdbcType=VARCHAR}
	,	TO_CHAR(SYSDATE,'YYYYMMDD')
	,	NULL
)

mapper.update:
/* intrEmpProc1021 : 사원 수정 처리 */
UPDATE EMP_INFO
	SET 	ORG_CD = #{setOrgCd, jdbcType=VARCHAR}
		,	RANK_CD = #{setRankCd, jdbcType=VARCHAR}
		,	IS_MALE = #{isMale, jdbcType=VARCHAR}
		,	MOB_NO = REPLACE(#{mobNo, jdbcType=VARCHAR},'-','')
		,	ADDR = #{addr, jdbcType=VARCHAR}
		,	ADDR_INFO = #{addrInfo, jdbcType=VARCHAR}
		,	EMAIL = #{email, jdbcType=VARCHAR}

			,	EMP_PWD = 	#{empPwd, jdbcType=VARCHAR}

WHERE EMP_IDX = #{empIdx, jdbcType=VARCHAR}

/* intrEmpProc1041 : 사원 삭제 처리 */
DELETE FROM EMP_INFO
	WHERE EMP_IDX = #{empIdx, jdbcType=VARCHAR}

/* intrEmpProc1051 : 사원 비밀번호 수정 처리 */
UPDATE EMP_INFO
	SET 	EMP_PWD = #{chngPwd, jdbcType=VARCHAR}
WHERE EMP_ID = #{chngPwdId, jdbcType=VARCHAR}
