mapper.select:
/* intrTaskInqy2011 : 업무일지 목록 조회 */ SELECT RESULT.* FROM ( SELECT T.TASK_ID , T.TASK_SNO , T.TASK_TITLE , T.TASK_DT , T.EMP_IDX , T.REG_DT , T.REG_TM , E.EMP_NM , E.ORG_CD , O.ORG_NM , R.RANK_NM , E.RANK_CD , COUNT(T.TASK_ID) OVER(PARTITION BY T.TASK_ID) AS ROW_CNT , ROW_NUMBER() OVER(PARTITION BY T.TASK_ID ORDER BY T.TASK_ID ASC, T.TASK_SNO ASC) AS ROW_NUM , COUNT(T.TASK_ID) OVER() AS LIST_CNT FROM TASK T INNER JOIN EMP_INFO E ON E.EMP_IDX = T.EMP_IDX INNER JOIN ORG O ON O.ORG_CD = E.ORG_CD INNER JOIN RANK R ON R.RANK_CD = E.RANK_CD WHERE 1=1
AND O.ORG_CD = #{orgCd, jdbcType=VARCHAR}
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND T.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND T.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
) RESULT ORDER BY RESULT.ROW_NUM ASC

mapper.delete:
/* intrTaskProc1021 : 업무일지 삭제 처리 */
DELETE FROM TASK
	WHERE EMP_IDX = #{empIdx, jdbcType=VARCHAR}
		AND TASK_DT = REPLACE(#{srchDt, jdbcType=VARCHAR},'-','')
