mapper.select:
/* intrAprvInqy1011 : 전자결재 목록 조회 */ WITH HIST AS ( SELECT APRV_ID, LISTAGG(APRV_IDX, '|') WITHIN GROUP (ORDER BY APRV_IDX) AS APRV_IDX FROM APRV_HIST AH WHERE AH.APRVTYPE_CD = 'TYPE_0030' GROUP BY APRV_ID, APRV_IDX ) SELECT SUB_DATA.* , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN , CASE WHEN SUB_DATA.TEMPTYPE_CD = 'Leav' THEN 'intr_aprv_detl_2010.jsp' WHEN SUB_DATA.TEMPTYPE_CD = 'Exp' THEN 'intr_aprv_detl_2011.jsp' WHEN SUB_DATA.TEMPTYPE_CD = 'Item' THEN 'intr_aprv_detl_2012.jsp' WHEN SUB_DATA.TEMPTYPE_CD = 'Corp' THEN 'intr_aprv_detl_2013.jsp' ELSE NULL END AS RETURN_URL FROM ( SELECT A.APRV_ID , A.APRV_TITLE , A.TEMP_CD , A.APRVSTEP_CD , CC_STEP.COMMCODE_NM AS APRVSTEP_NM , A.CURR_APRV_SNO , T.TEMPTYPE_CD , A.REG_IDX , E.EMP_NM , O.ORG_NM , R.RANK_NM , A.REG_DT , A.REG_TM , RANK() OVER(ORDER BY A.APRV_ID DESC) AS NUM , COUNT(A.APRV_ID) OVER() AS LIST_CNT , (SELECT COUNT(FILE_ID) FROM FILE_INFO WHERE FILE_ID = A.APRV_ID AND USE_YN = 'Y' ) AS FILE_CNT FROM APRV_MAST A INNER JOIN APRV_HIST AH ON AH.APRV_ID = A.APRV_ID AND AH.APRV_SNO = A.CURR_APRV_SNO LEFT OUTER JOIN HIST H ON A.APRV_ID = H.APRV_ID LEFT OUTER JOIN EMP_INFO E ON E.EMP_IDX = A.REG_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD AND O.USE_YN = 'Y' LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD AND R.USE_YN = 'Y' LEFT OUTER JOIN TEMPLATE T ON T.TEMP_CD = A.TEMP_CD LEFT OUTER JOIN COMMCODE CC_STEP ON CC_STEP.COMMCODE_CD = A.APRVSTEP_CD WHERE 1=1 AND (A.REG_IDX = #{empIdx, jdbcType=VARCHAR} OR INSTR(H.APRV_IDX, #{empIdx, jdbcType=VARCHAR}) > 0 OR AH.APRV_IDX = #{empIdx, jdbcType=VARCHAR})
AND REGEXP_REPLACE(LOWER(A.APRV_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND A.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND A.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
AND REGEXP_REPLACE(LOWER(O.ORG_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{orgNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(R.RANK_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{rankNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchIdx, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND A.APRVSTEP_CD = #{srchStepCd, jdbcType=VARCHAR}
) SUB_DATA WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.NUM DESC

/* intrAprvInqy3011 : 연차 공유 조회 */ SELECT AM.APRV_ID , AM.APRV_TITLE , AM.APRVSTEP_CD , CC_STEP.COMMCODE_NM AS APRVSTEP_NM , AL.LEAV_IDX , AL.LEAVTYPE_CD , CC_LEAV.COMMCODE_NM AS LEAVTYPE_NM , AL.LEAV_SDT , AL.LEAV_EDT , AL.LEAV_DAYS , AL.LEAV_RSN , AL.LEAV_SUBST , AM.APRV_ID AS ID , '[' || CC_LEAV.COMMCODE_NM || '] ' || AM.APRV_TITLE || '_' || E.EMP_NM AS "TITLE" , TO_CHAR(TO_DATE(AL.LEAV_SDT,'YYYY-MM-DD'), 'YYYY-MM-DD') || ' 00:00:00' AS "START" , TO_CHAR(TO_DATE(AL.LEAV_EDT,'YYYY-MM-DD'), 'YYYY-MM-DD') || ' 24:00:00' AS "END" FROM APRV_MAST AM INNER JOIN APRV_REL_LEAV AL ON AM.APRV_ID = AL.APRV_ID AND AM.APRVSTEP_CD = 'STEP_0020' LEFT OUTER JOIN EMP_INFO E ON E.EMP_IDX = AL.LEAV_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD LEFT OUTER JOIN COMMCODE CC_STEP ON CC_STEP.COMMCODE_CD = AM.APRVSTEP_CD LEFT OUTER JOIN COMMCODE CC_LEAV ON CC_LEAV.COMMCODE_CD = AL.LEAVTYPE_CD WHERE 1=1
AND AL.LEAVTYPE_CD = #{leavCd, jdbcType=VARCHAR}
AND REGEXP_REPLACE(LOWER(O.ORG_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{orgNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(R.RANK_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{rankNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
ORDER BY AM.APRV_ID ASC

mapper.update:
/* intrAprvProc2011 : 결재 단계 수정 */
UPDATE APRV_MAST
	SET APRVSTEP_CD = #{stepCd, jdbcType=VARCHAR}
WHERE APRV_ID = #{sequenceId, jdbcType=VARCHAR}

/* intrAprvProc2012 : 결재 처리 유형 수정 */
UPDATE APRV_HIST
	SET RSLTTYPE_CD = #{rslttypeCd, jdbcType=VARCHAR}
		,	RSLT_NOTE =	#{rsltNote, jdbcType=VARCHAR}
		,	RSLT_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
		,	RSLT_TM = TO_CHAR(SYSDATE,'HH24MISS')
	WHERE APRV_ID = #{sequenceId, jdbcType=VARCHAR}
		AND APRV_SNO = #{currAprvSno, jdbcType=VARCHAR}

/* intrAprvProc2013 : 결재 일련번호 수정 */
UPDATE APRV_MAST
	SET CURR_APRV_SNO = #{currAprvSno, jdbcType=VARCHAR}
WHERE APRV_ID = #{sequenceId, jdbcType=VARCHAR}

/* intrAprvProc4013 : 결재선 마스터 (APRV_LINE_MAST) 수정 */
UPDATE APRV_LINE_MAST
	SET APRVLINE_NM = #{aprvlineNm, jdbcType=VARCHAR}
WHERE APRVLINE_ID = #{sequenceId, jdbcType=VARCHAR}

mapper.delete:
/* intrAprvProc4014 : 결재선 마스터 (APRV_LINE_MAST) 삭제 */
DELETE FROM APRV_LINE_MAST
	WHERE APRVLINE_ID = #{sequenceId, jdbcType=VARCHAR}

/* intrAprvProc4015 : 결재선 상세 (APRV_LINE_DETL) 삭제 */
DELETE FROM APRV_LINE_DETL
	WHERE APRVLINE_ID = #{sequenceId, jdbcType=VARCHAR}
