mapper.select:
/* intrMtgInqy1011 : 회의 목록 조회 */ SELECT SUB_DATA.NUM , SUB_DATA.MTG_CD , SUB_DATA.MTG_TITLE , SUB_DATA.MTG_DT , SUB_DATA.MTG_STM , SUB_DATA.MTG_ETM , SUB_DATA.ORG_NM , SUB_DATA.RANK_NM , SUB_DATA.MTG_IDX , SUB_DATA.EMP_NM , SUB_DATA.REG_DT , SUB_DATA.REG_TM , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN , SUB_DATA.LIST_CNT FROM ( SELECT M.MTG_CD , M.MTG_TITLE , M.MTG_DT , M.MTG_STM , M.MTG_ETM , M.MTG_IDX , E.EMP_NM , O.ORG_NM , R.RANK_NM , M.REG_DT , M.REG_TM , RANK() OVER(ORDER BY M.MTG_CD DESC) AS NUM , (SELECT COUNT(FILE_ID) FROM FILE_INFO WHERE FILE_ID = M.MTG_CD AND USE_YN = 'Y' ) AS FILE_CNT , COUNT(M.MTG_CD) OVER() AS LIST_CNT FROM MEETING M LEFT OUTER JOIN EMP_INFO E ON M.MTG_IDX = E.EMP_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD AND O.USE_YN = 'Y' LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD AND R.USE_YN = 'Y' WHERE M.USE_YN = 'Y'
AND REGEXP_REPLACE(LOWER(M.MTG_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND M.MTG_DT = REPLACE(#{srchDt, jdbcType=VARCHAR},'-','')
AND REGEXP_REPLACE(LOWER(O.ORG_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{orgNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(R.RANK_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{rankNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchIdx, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
) SUB_DATA WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.NUM DESC

/* intrMtgInqy2021 : 캘린더 목록 조회 */ SELECT M.MTG_CD , M.MTG_TITLE , M.MTG_CONT , M.MTG_IDX , M.MTG_DT , E.EMP_NM , O.ORG_NM , R.RANK_NM , M.MTG_STM , M.MTG_ETM FROM MEETING M LEFT OUTER JOIN EMP_INFO E ON M.MTG_IDX = E.EMP_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD WHERE 1=1
AND M.MTG_DT = #{mtgDt, jdbcType=VARCHAR}
AND M.MTG_DT = TO_CHAR(SYSDATE,'YYYYMMDD')

mapper.update:
/* intrMtgProc1021 : 회의 삭제 처리 (수정)  */
UPDATE MEETING
	SET USE_YN = 'N'
WHERE MTG_CD = #{sequenceId, jdbcType=VARCHAR}

/* intrMtgProc1031 : 회의 수정 처리 */
UPDATE MEETING
	SET 	MTG_TITLE = #{mtgTitle, jdbcType=VARCHAR}
		,	MTG_CONT = #{mtgCont, jdbcType=VARCHAR}
		,	MTG_IDX = #{empIdx, jdbcType=VARCHAR}
		,	MTG_DT = REPLACE(#{mtgDt, jdbcType=VARCHAR},'-','')
		,	MTG_STM = REPLACE(#{mtgStm, jdbcType=VARCHAR},':','')
		,	MTG_ETM = REPLACE(#{mtgEtm, jdbcType=VARCHAR},':','')
		,	MTG_LOC = #{mtgLoc, jdbcType=VARCHAR}
		,	MTG_MBR = #{mtgMbr, jdbcType=VARCHAR}
WHERE MTG_CD = #{sequenceId, jdbcType=VARCHAR}
