mapper.select:
/* intrProjInqy1011 : 프로젝트 목록 조회 */ SELECT SUB_DATA.NUM , SUB_DATA.PROJ_CD , SUB_DATA.PROJ_TITLE , SUB_DATA.PROJ_CONT , SUB_DATA.STATUS , CC_STAT.COMMCODE_NM AS STATUS_NM , SUB_DATA.PROJ_SDT , SUB_DATA.PROJ_EDT , SUB_DATA.ORG_NM , SUB_DATA.RANK_NM , SUB_DATA.OWNER , SUB_DATA.OWNER_NM , SUB_DATA.REG_DT , SUB_DATA.REG_TM , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN , SUB_DATA.LIST_CNT FROM ( SELECT P.PROJ_CD , P.PROJ_TITLE , P.PROJ_CONT , P.PROJ_SDT , P.PROJ_EDT , P.STATUS , P.OWNER , P.EMP_IDX , E.EMP_NM AS OWNER_NM , O.ORG_NM , R.RANK_NM , P.REG_DT , P.REG_TM , RANK() OVER(ORDER BY P.PROJ_CD DESC) AS NUM , (SELECT COUNT(FILE_ID) FROM FILE_INFO WHERE FILE_ID = P.PROJ_CD AND USE_YN = 'Y' ) AS FILE_CNT , COUNT(P.PROJ_CD) OVER() AS LIST_CNT FROM PROJECT P LEFT OUTER JOIN EMP_INFO E ON P.OWNER = E.EMP_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD AND O.USE_YN = 'Y' LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD AND R.USE_YN = 'Y' WHERE P.USE_YN = 'Y'
AND REGEXP_REPLACE(LOWER(P.PROJ_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
(AND P.PROJ_SDT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','') OR P.PROJ_EDT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-',''))
AND REGEXP_REPLACE(LOWER(O.ORG_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{orgNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(R.RANK_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{rankNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchIdx, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND P.STATUS = #{srchStatCd, jdbcType=VARCHAR}
) SUB_DATA LEFT OUTER JOIN COMMCODE CC_STAT ON CC_STAT.COMMCODE_CD = SUB_DATA.STATUS WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.NUM DESC

mapper.update:
/* intrProjProc1021 : 프로젝트 삭제 처리 (수정)  */
UPDATE PROJECT
	SET USE_YN = 'N'
WHERE PROJ_CD IN

	#{deleteIdx}

mapper.delete:
/* intrProjProc1022 : 프로젝트 삭제 처리 (삭제) */
DELETE FROM PROJECT
WHERE PROJ_CD IN

	#{deleteIdx}

mapper.update:
/* intrProjProc1031 : 프로젝트 수정 처리 */
UPDATE PROJECT
	SET 	PROJ_TITLE = #{projTitle, jdbcType=VARCHAR}
		,	PROJ_CONT = #{projCont, jdbcType=VARCHAR}
		,	STATUS = #{status, jdbcType=VARCHAR}
		,	PROJ_SDT = REPLACE(#{projSdt, jdbcType=VARCHAR},'-','')
		,	PROJ_EDT = REPLACE(#{projEdt, jdbcType=VARCHAR},'-','')
		,	OWNER = #{empPcd, jdbcType=VARCHAR}
		,	DEPOSIT = #{deposit, jdbcType=VARCHAR}
		,	TAG = #{tag, jdbcType=VARCHAR}
		,	EMP_IDX = #{empIdx, jdbcType=VARCHAR}
		,	REG_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
		,	REG_TM = TO_CHAR(SYSDATE,'HH24MISS')
WHERE PROJ_CD = #{sequenceId, jdbcType=VARCHAR}
