mapper.select:
/* intrBoardInqy1011 : 공지사항 목록 조회 */ SELECT SUB_DATA.NUM , SUB_DATA.BRD_ID , SUB_DATA.BRD_TITLE , SUB_DATA.EMP_NM , SUB_DATA.ORG_NM , SUB_DATA.RANK_NM , SUB_DATA.LIST_CNT , SUB_DATA.REG_DT , SUB_DATA.READ_HIT , CASE WHEN SUB_DATA.FILE_CNT > 0 THEN 'Y' ELSE 'N' END AS FILE_YN , SUB_DATA.USE_YN FROM ( SELECT B.BRD_ID , B.BRD_TITLE , B.EMP_IDX , E.EMP_NM , O.ORG_NM , R.RANK_NM , B.REG_DT , B.REG_TM , B.READ_HIT , RANK() OVER(ORDER BY B.BRD_ID DESC) AS NUM , COUNT(B.BRD_ID) OVER() AS LIST_CNT , (SELECT COUNT(FILE_ID) FROM WORKFILE WHERE FILE_ID = B.BRD_ID AND USE_YN = 'Y' ) AS FILE_CNT , B.USE_YN FROM BOARD B LEFT OUTER JOIN EMPINFO E ON B.EMP_IDX = E.EMP_IDX LEFT OUTER JOIN ORG O ON O.ORG_CD = E.ORG_CD AND O.USE_YN = 'Y' LEFT OUTER JOIN RANK R ON R.RANK_CD = E.RANK_CD AND R.USE_YN = 'Y' WHERE 1 = 1
AND REGEXP_REPLACE(LOWER(B.BRD_TITLE),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND B.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND B.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
AND REGEXP_REPLACE(LOWER(O.ORG_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{orgNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND REGEXP_REPLACE(LOWER(R.RANK_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{rankNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND B.USE_YN = #{useCd, jdbcType=VARCHAR}
AND REGEXP_REPLACE(LOWER(E.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchIdx, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
) SUB_DATA WHERE 1=1
AND SUB_DATA.NUM BETWEEN #{sIdx, jdbcType=VARCHAR} AND #{eIdx, jdbcType=VARCHAR}
ORDER BY SUB_DATA.REG_DT DESC, SUB_DATA.REG_TM DESC

mapper.update:
/* intrBoardProc1021 : 공지사항 삭제 처리 (수정)  */
UPDATE BOARD
	SET USE_YN = 'N'
WHERE BRD_ID IN

	#{delIdx}

mapper.delete:
/* intrBoardProc1022 : 공지사항 삭제 처리 (삭제) */
DELETE FROM BOARD
WHERE BRD_ID IN

	#{delIdx}

mapper.update:
/* intrBoardProc1031 : 공지사항 수정 처리 */
UPDATE BOARD
	SET 	BRD_TITLE = #{brdTitle, jdbcType=VARCHAR}
		,	BRD_CONT = #{brdCont, jdbcType=VARCHAR}
		,	USE_YN = #{useYn, jdbcType=VARCHAR}
		,	REG_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
		,	REG_TM = TO_CHAR(SYSDATE,'HH24MISS')
WHERE BRD_ID = #{sequenceId, jdbcType=VARCHAR}

/* intrBoardProc1032 : 공지사항 조회수 증가 */
UPDATE BOARD
	SET 	READ_HIT = READ_HIT + 1
WHERE BRD_ID = #{sequenceId, jdbcType=VARCHAR}
