mapper.select:
/* intrMainInqy1010 : 메뉴 조회 */ WITH A_DATA AS ( SELECT DATA.* FROM ( SELECT AM.MENU_CD FROM INTR_AUTH A INNER JOIN INTR_EMP E ON E.EMP_IDX = #{empIdx, jdbcType=VARCHAR} AND A.USE_YN = 'Y' INNER JOIN INTR_AUTH_EMP AE ON AE.EMP_IDX = E.EMP_IDX INNER JOIN INTR_AUTH_MENU AM ON AM.AUTH_CD = AE.AUTH_CD GROUP BY AM.MENU_CD ) DATA ) SELECT DATA.* , LEVEL AS LV FROM ( SELECT M.MENU_CD , M.MENUTYPE_CD , M.MENU_NM , M.UPPR_MENU_CD , UPPR_M.MENU_NM AS UPPR_MENU_NM , M.MAPPING_ID , M.USE_YN , M.DISP_ORDER , M.REMARK FROM A_DATA INNER JOIN INTR_MENU M ON M.MENU_CD = A_DATA.MENU_CD LEFT OUTER JOIN INTR_MENU UPPR_M ON UPPR_M.MENU_CD = M.UPPR_MENU_CD WHERE 1=1
AND M.MENUTYPE_CD = '0'
AND M.MENUTYPE_CD = '1'
) DATA START WITH DATA.UPPR_MENU_CD IS NULL CONNECT BY PRIOR DATA.MENU_CD = DATA.UPPR_MENU_CD ORDER SIBLINGS BY DATA.DISP_ORDER, DATA.MENU_CD

/* intrLoginInqy1030 : 사용자 정보 찾기 */ SELECT EMP_IDX , DEPT_CD , GRADE_CD , EMP_NM , EMP_GENDER , EMP_PHONE , EMP_ADDR , EMP_ADDR_INFO , EMP_EMAIL , EMP_ID , EMP_PWD , EMP_HIRE_DATE , EMP_RESI_DATE FROM INTR_EMP WHERE 1=1 AND UPPER(EMP_EMAIL) = UPPER(#{findEmail, jdbcType=VARCHAR})
AND UPPER(EMP_ID) = UPPER(#{findId, jdbcType=VARCHAR})
AND EMP_NM = #{findNm, jdbcType=VARCHAR}
