mapper.select:
/* intrScheInqyDao1010 : 일정 목록 조회 */ SELECT APRV.CONT_ID , APRV.CURR_CONT_SID , APRV.EMP_IDX , DEPT.DEPT_CD , DEPT.DEPT_NM , GRADE.GRADE_CD , GRADE.GRADE_NM , '[연차] '||DEPT.DEPT_NM||' '||EMP.EMP_NM || APRV.APRV_TITLE AS "TITLE" , TO_CHAR(TO_DATE(APRV.EFCT_SDT,'YYYY-MM-DD'), 'YYYY-MM-DD') || ' 00:00:00' AS "START" , TO_CHAR(TO_DATE(APRV.EFCT_EDT,'YYYY-MM-DD'), 'YYYY-MM-DD') || ' 24:00:00' AS "END" , APRV.CONT_ID AS "ID" , APRV.USE_YN , APRV.DISP_ORDER , APRV.REMARK FROM INTR_APRV APRV INNER JOIN INTR_APRV_LINE LINE ON LINE.CONT_ID = APRV.CONT_ID AND LINE.LAST_APRV_YN = 'Y' AND LINE.APRV_RSLT_DT IS NOT NULL LEFT OUTER JOIN INTR_EMP EMP ON EMP.EMP_IDX = APRV.EMP_IDX LEFT OUTER JOIN INTR_DEPT DEPT ON DEPT.DEPT_CD = EMP.DEPT_CD LEFT OUTER JOIN INTR_GRADE GRADE ON GRADE.GRADE_CD = EMP.GRADE_CD WHERE 1=1
AND REGEXP_REPLACE(LOWER(EMP.EMP_NM),'[^0-9가-힣A-Za-z]*','') LIKE '%'||REGEXP_REPLACE(LOWER(#{srchNm, jdbcType=VARCHAR}),'[^0-9가-힣A-Za-z]*','')||'%'
AND APRV.REG_DT >= REPLACE(#{srchSdt, jdbcType=VARCHAR},'-','')
AND APRV.REG_DT <= REPLACE(#{srchEdt, jdbcType=VARCHAR},'-','')
AND DEPT.DEPT_CD = #{deptCd, jdbcType=VARCHAR}
AND GRADE.GRADE_CD = #{gradeCd, jdbcType=VARCHAR}
